mapper.update:
UPDATE clazz
name = #{name}, room = #{room}, begin_date = #{beginDate}, end_date = #{endDate}, master_id = #{masterId}, subject = #{subject}, update_time = #{updateTime},
WHERE id = #{id}

mapper.select:
select c.*, e.name masterName from clazz as c left join emp as e on c.master_id = e.id
c.name like concat('%',#{name},'%')
and c.end_date between #{begin} and #{end}
