mapper.update:
UPDATE student
name = #{name}, no = #{no}, gender = #{gender}, phone = #{phone}, id_card = #{idCard}, address = #{address}, degree = #{degree}, graduation_date = #{graduationDate}, clazz_id = #{clazzId}, violation_count = #{violationCount}, violation_score = #{violationScore}, update_time = #{updateTime},
WHERE id = #{id}

mapper.select:
select s.id, s.name, s.no, s.gender, s.phone, s.id_card AS idCard, s.address, s.degree, s.graduation_date AS graduationDate, s.clazz_id AS clazzId, c.name AS clazzName, s.violation_count AS violationCount, s.violation_score AS violationScore, s.create_time, s.update_time from student as s left join clazz as c on s.clazz_id = c.id
s.name like concat('%',#{name},'%')
and s.degree = #{degree}
and s.clazz_id = #{clazzId}
